  real&lt;lower=0&gt; gamma;
  real&lt;lower=0&gt; beta;
}

parameters {

}


transformed parameters {
  vector&lt;lower=0&gt;[N] daily_inf;
  vector&lt;lower=0&gt;[N+1] S;
  vector&lt;lower=0&gt;[N+1] I;
  vector&lt;lower=0&gt;[N+1] R;
  vector&lt;lower=0&gt;[N+1] CI;

