  real&lt;lower=0&gt; sigma_mu_t;   real&lt;lower=0&gt; sigma_mu_I;  real&lt;lower=0&gt; sigma_mu_D;
  vector[G] mu_t_raw; vector[G] mu_I_raw;  vector[G] mu_D_raw;

